    import { useState } from 'react';

    export default function Form() {
      const [form, setForm] = useState({
        firstName: 'Barbara',
        lastName: 'Hepworth',
        email: 'bhepworth@sculpture.com',
      });

      return (
        <>
          <label>
            First name:
            <input
              value={form.firstName}
              onChange={e => {
