          <label>
            Email:
            <input
              value={form.email}
              onChange={e => {
                setForm({
                  ...form,
                  email: e.target.value
                });
              }}
            />
          </label>
          <p>
            {form.firstName}{' '}
            {form.lastName}{' '}
            ({form.email})
          </p>
        </>
      );
    }



